import { VStack, Center, Box, Pressable, HStack, Badge, Spacer, Flex } from 'native-base';
import React from 'react';
import { SafeAreaView, Text, Image, View, StyleSheet, TouchableOpacity, ImageBackground } from 'react-native';

const SettingsApp = () => {
  return (
    <SafeAreaView>
      <Box alignItems="center">
        <Pressable maxW="800">
          {({
            isHovered,
            isFocused,
            isPressed
          }) => {
            return <Box bg={isPressed ? "coolGray.200" : isHovered ? "coolGray.200" : "coolGray.100"} maxW="100%" style={{
              transform: [{
                scale: isPressed ? 0.96 : 1
              }]
            }} p="5" rounded="8" shadow={3} borderWidth="1" borderColor="coolGray.300">
              <HStack alignItems="center">
                <Badge colorScheme="darkBlue" _text={{
                  color: "white"
                }} variant="solid" rounded="4">
                  Changer la langue
                </Badge>
                <Spacer />
                <Text fontSize={10} color="coolGray.800">
                  Fran√ßais
                </Text>
              </HStack>
            </Box>;
          }}
        </Pressable>
      </Box>
    </SafeAreaView>
  )
}

export default SettingsApp;
